import Aside from "@/components/Aside";
import Header from "@/components/Header";
export default {
  name: "manage",
  data() {
    return {
      tableData: [],
      collapseBtnclass: 'el-icon-s-fold',
      isCollapse: false,
      sideWidth: 200,
      logoTextShow: true,
      dialogFormVisible: false,
      multipleSelection: [],
      form: {},
      username: "",
      email: "",
      address: "",
      total: 0,
      size: 5,
      pageNum: 1
    };
  },
  created() {
    this.load();
  },
  components: {
    Aside,
    Header
  },
  methods: {
    collapse() {
      this.isCollapse = !this.isCollapse;
      if (this.isCollapse) {
        this.sideWidth = 64;
        this.collapseBtnclass = 'el-icon-s-unfold';
        this.logoTextShow = false;
      } else {
        this.sideWidth = 200;
        this.collapseBtnclass = 'el-icon-s-fold';
        this.logoTextShow = true;
      }
    },
    load() {
      this.request.get("/user/page", {
        params: {
          pageNum: this.pageNum,
          pageSize: this.size,
          username: this.username,
          email: this.email,
          address: this.address
        }
      }).then(res => {
        this.tableData = res.data.records;
        this.total = res.data.total;
        this.size = res.data.size;
      });

      // fetch("http://localhost:9090/user/page?pageNum=" + this.pageNum + "&pageSize=" + this.size+"&username="+this.username+"&email="+this.email).then(res => res.json()).then(res => {
      //   this.tableData = res.data.records;
      //   this.total = res.data.total;
      //   this.size = res.data.size;
      // })
    },

    handleSizeChange(pageSize) {
      this.size = pageSize;
      this.load();
    },
    handleCurrentChange(pageNum) {
      this.pageNum = pageNum;
      this.load();
    },
    handleadd() {
      this.dialogFormVisible = true;
      this.form = {};
    },
    handleedit(row) {
      this.form = row;
      this.dialogFormVisible = true;
    },
    del(id) {
      this.request.delete("/user/" + id, this.form).then(res => {
        if (res.data) {
          this.$message.success("删除成功！");
          this.dialogFormVisible = false;
          this.load();
        } else {
          this.$message.error("删除失败！");
        }
      });
    },
    delbatch() {
      let ids = this.multipleSelection.map(v => v.id); //存入id
      this.request.post("/user/del/batch", ids).then(res => {
        if (res.data) {
          this.$message.success("批量删除成功！");
          this.dialogFormVisible = false;
          this.load();
        } else {
          this.$message.error("批量删除失败！");
        }
      });
    },
    handleSelectionChange(val) {
      //多选
      this.multipleSelection = val;
    },
    save() {
      this.request.post("/user", this.form).then(res => {
        if (res.data) {
          this.$message.success("保存成功！");
          this.dialogFormVisible = false;
          this.load();
        } else {
          this.$message.error("保存失败！");
        }
      });
    },
    reset() {
      this.username = "";
      this.email = "";
      this.address = "";
    }
  }
};
